import React, { Component } from "react";

import moment from "moment";
import { Calendar, momentLocalizer } from "react-big-calendar";
import request from "superagent";
import Popup from "reactjs-popup";

import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = momentLocalizer(moment);

export class CalendarPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      eventsList: [],
      openEvent: false,
      event: ""
    };
  }

  componentDidMount() {
    //request google calendar events
  }

  onSelectEvent(event) {
    this.setState({ openEvent: true, event: event });
  }

  render() {
    return (
      <div>
        <h1>Calendar</h1>
        <br />

        <Calendar
          localizer={localizer}
          events={this.state.eventsList}
          startAccessor="start"
          endAccessor="end"
          onSelectEvent={event => this.onSelectEvent(event)}
          style={{ height: "420px" }}
        />
        <Popup
          modal
          closeOnDocumentClick
          open={this.state.openEvent}
          onClose={() => {
            this.setState({ openEvent: false });
          }}
        >
          <h1 style={{ textAlign: "center" }}>{this.state.event.title}</h1>
          <br />
          <br />
          <ul>
            <li>
              {this.state.event.start && this.state.event.end && (
                <div>
                  Date(s): {this.state.event.start.toString()} -{" "}
                  {this.state.event.end.toString()}
                </div>
              )}
            </li>
            <br />
            <li>
              {this.state.event.description && (
                <div>Description: {this.state.event.description}</div>
              )}
            </li>
          </ul>
        </Popup>
      </div>
    );
  }
}

export default CalendarPage;
